  featuredProducts = [
    {
      id: 1,
      name: 'Royal Oud',
      price: 999,
      image: 'assets/images/product/1.jpg',
      category: 'Perfume'
    },
    {
      id: 2,
      name: 'Mystic Rose',
      price: 799,
      image: 'assets/images/product/2.jpg',
      category: 'Attar'
    },
    {
      id: 3,
