import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-newsletter',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <section class="newsletter">
      <div class="container">
        <div class="newsletter-content">
          <h2>Join Our Newsletter</h2>
          <p>Subscribe to receive updates, exclusive offers, and more.</p>
          
          <form class="newsletter-form" (ngSubmit)="onSubmit()">
            <div class="input-group">
              <input 
                type="email" 
                [(ngModel)]="email" 
                name="email"
                class="form-control" 
                placeholder="Your email"
                required
              >
              <button type="submit" class="subscribe-btn">Subscribe</button>
            </div>
          </form>
        </div>
      </div>
    </section>
  `,
  styles: [`
    .newsletter {
      padding: 60px 0;
      background-color: #f8f9fa;
      text-align: center;
    }

    .newsletter-content {
      max-width: 600px;
      margin: 0 auto;
      padding: 0 20px;
    }

    h2 {
      font-size: 2.5rem;
      font-weight: 700;
      margin-bottom: 15px;
      color: #333;
    }

    p {
      font-size: 1.1rem;
      color: #666;
      margin-bottom: 30px;
    }

    .newsletter-form {
      margin-top: 20px;
    }

    .input-group {
      display: flex;
      gap: 10px;
      max-width: 500px;
      margin: 0 auto;
    }

    .form-control {
      flex: 1;
      padding: 12px 20px;
      font-size: 1rem;
      border: 1px solid #ddd;
      border-radius: 25px;
      outline: none;
      transition: border-color 0.3s ease;
    }

    .form-control:focus {
      border-color: #8a2be2;
    }

    .subscribe-btn {
      padding: 12px 30px;
      font-size: 1rem;
      color: #fff;
      background: linear-gradient(45deg, #8a2be2, #4a90e2);
      border: none;
      border-radius: 25px;
      cursor: pointer;
      transition: transform 0.3s ease, box-shadow 0.3s ease;
    }

    .subscribe-btn:hover {
      transform: translateY(-2px);
      box-shadow: 0 4px 12px rgba(138, 43, 226, 0.2);
    }

    @media (max-width: 576px) {
      .input-group {
        flex-direction: column;
      }

      .form-control {
        width: 100%;
      }

      .subscribe-btn {
        width: 100%;
      }

      h2 {
        font-size: 2rem;
      }
    }
  `]
})
export class NewsletterComponent {
  email: string = '';

  onSubmit() {
    if (this.email) {
      console.log('Subscribing email:', this.email);
      // Here you would typically call a service to handle the subscription
      this.email = ''; // Reset form after submission
    }
  }
}
