import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { CarouselComponent } from '../../components/carousel/carousel.component';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, RouterModule, CarouselComponent],
  template: `
    <!-- Breadcrumb -->
    <nav class="breadcrumb-wrapper">
      <div class="container">
        <ol class="breadcrumb">
          <li class="breadcrumb-item"><a routerLink="/">Home</a></li>
          <li class="breadcrumb-item active">New Arrivals</li>
        </ol>
      </div>
    </nav>

    <!-- Hero Carousel -->
    <section class="hero-section">
      <app-carousel></app-carousel>
    </section>

    <!-- New Arrivals Section -->
    <section class="new-arrivals">
      <div class="container">
        <div class="section-header text-center">
          <h2>New Arrivals</h2>
          <p>Discover our latest collection of premium fragrances</p>
        </div>
        <div class="row">
          <!-- Product Cards -->
          <div class="col-lg-3 col-md-6" *ngFor="let product of newArrivals">
            <div class="product-card">
              <div class="product-image">
                <img [src]="product.image" [alt]="product.name">
                <div class="product-actions">
                  <button class="action-btn" title="Quick View">
                    <i class="fas fa-eye"></i>
                  </button>
                  <button class="action-btn" title="Add to Cart">
                    <i class="fas fa-shopping-cart"></i>
                  </button>
                </div>
              </div>
              <div class="product-content">
                <h3 class="product-title">{{product.name}}</h3>
                <div class="product-category">{{product.category}}</div>
                <div class="product-price">₹{{product.price}}</div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  `,
  styles: [`
    .breadcrumb-wrapper {
      background-color: #f8f9fa;
      padding: 1rem 0;
      margin-bottom: 1rem;

      .breadcrumb {
        margin: 0;
        background: transparent;

        .breadcrumb-item {
          font-size: 14px;
          
          a {
            color: #666;
            text-decoration: none;
            
            &:hover {
              color: #007bff;
            }
          }

          &.active {
            color: #333;
          }
        }
      }
    }

    .hero-section {
      height: 60vh;
      margin-bottom: 4rem;
    }

    .new-arrivals {
      padding: 4rem 0;
      background-color: #fff;

      .section-header {
        margin-bottom: 3rem;

        h2 {
          font-size: 2.5rem;
          font-weight: 700;
          margin-bottom: 1rem;
        }

        p {
          color: #666;
          font-size: 1.1rem;
        }
      }

      .product-card {
        background: #fff;
        border-radius: 8px;
        box-shadow: 0 2px 15px rgba(0,0,0,0.05);
        margin-bottom: 2rem;
        transition: transform 0.3s ease;

        &:hover {
          transform: translateY(-5px);

          .product-actions {
            opacity: 1;
          }
        }

        .product-image {
          position: relative;
          padding-top: 100%;
          overflow: hidden;
          border-radius: 8px 8px 0 0;

          img {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            object-fit: cover;
          }

          .product-actions {
            position: absolute;
            bottom: 1rem;
            left: 0;
            right: 0;
            display: flex;
            justify-content: center;
            gap: 0.5rem;
            opacity: 0;
            transition: opacity 0.3s ease;

            .action-btn {
              background: white;
              border: none;
              width: 40px;
              height: 40px;
              border-radius: 50%;
              display: flex;
              align-items: center;
              justify-content: center;
              cursor: pointer;
              transition: all 0.3s ease;

              &:hover {
                background: #007bff;
                color: white;
              }
            }
          }
        }

        .product-content {
          padding: 1.5rem;

          .product-title {
            font-size: 1.1rem;
            font-weight: 600;
            margin-bottom: 0.5rem;
          }

          .product-category {
            color: #666;
            font-size: 0.9rem;
            margin-bottom: 0.5rem;
          }

          .product-price {
            font-size: 1.2rem;
            font-weight: 700;
            color: #007bff;
          }
        }
      }
    }

    @media (max-width: 991px) {
      .hero-section {
        height: 50vh;
      }

      .new-arrivals {
        .section-header {
          h2 {
            font-size: 2rem;
          }
        }
      }
    }
  `]
})
export class HomeComponent {
  newArrivals = [
    {
      id: 1,
      name: 'Office Hour',
      category: 'Perfume',
      price: 999,
      image: 'assets/images/products/office-hour.jpg'
    },
    {
      id: 2,
      name: 'Pine Elixir',
      category: 'Perfume',
      price: 899,
      image: 'assets/images/products/pine-elixir.jpg'
    },
    {
      id: 3,
      name: 'Violet Essence',
      category: 'Perfume',
      price: 799,
      image: 'assets/images/products/violet-essence.jpg'
    },
    {
      id: 4,
      name: 'Marine Fresh',
      category: 'Perfume',
      price: 899,
      image: 'assets/images/products/marine-fresh.jpg'
    }
  ];
}
